mapper.select:
select id,user_id,category_id,subject,rate,rate_times as ratetimes,views,top,allow_comment as allowcomment,status,deleted,cms_article.created_date as createddate,cms_article.last_modified_date as lastmodifieddate
from cms_article
order by ${Sort} ${Direction}

select id,subject,message,digest,author,category_name,rate,rate_times,views,created_date,last_modified_date,top,status
from cms_article

    
        author=#{author}
    
    
        and top=#{top}
    
    
        and status=#{status}
    
    
        and keyword=#{keyword}
    
    
        and deleted=#{deleted}
    

order by ${Sort} ${Direction}
limit 500
