INSERT INTO `cms_agency` (`id`, `title`,`category_id`, `image_url`, `introduction`, `rate`, `deleted`, `created_date`, `last_modified_date`) VALUES
(1, '金融风险管理研究所', 1, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 09:11:03', '2012-04-21 09:07:47'),
(2, '金融服务计算研究所', 2, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 09:11:03', '2012-04-21 09:07:47'),
(3, '计算金融研究所', 3, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 09:11:03', '2012-04-21 09:07:47'),
(4, '金融信息安全研究所', 4, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 09:11:03', '2012-04-21 09:07:47'),
(5, '金融数据管理研究所', 5, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 09:11:03', '2012-04-21 09:07:47'),
(6, '金融服务创新研究所', 6, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 09:11:03', '2012-04-21 09:07:47');


